import React, {useContext, useEffect, useState} from "react";

import './styleParticipants.css';

import {
    Container,
    Row,
    Col,
    Label,
    Input,
    TabContent,
    TabPane,
    FormGroup,
    ModalHeader,
    ModalBody,
    Nav,
    NavItem,
    NavLink,
    Form
} from "reactstrap";
import CommonLayout from "../../containers/common/common-layout";

import {useRouter} from "next/router";

import Axios, * as others from "axios";

import {
    Grid,
    Link,
    List,
    ListItem,
    Typography,
    Card,
    Button,
    Badge,
    CardContent,
    TextField,
    CircularProgress
} from "@material-ui/core";
import {ListGroupItem, ListGroup} from "reactstrap";
import {useParams} from "react-router-dom";

function CoursesDetails(props) {
    const router = useRouter();
    const {id} = router.query
    const [courses, setCourses] = useState({});
    const [coursesTeachers, setCoursesTeachers] = useState([]);
    const [usersss, setUsersss] = useState({});
    const [newName, setNewName] = useState("");
    const [newType, setNewType] = useState("");
    const [newUser, setNewUser] = useState("");
    const [coursesList, setCoursesList] = useState([]);
    const [modal, setModal] = useState();
    const [activeTab, setActiveTab] = useState("1");
    /*const listItems = coursesTeachers.map((number) =>
  <li>{number}</li>
  );*/

    const toggle = () => {
        setModal(!modal);
    };

    if (!courses) {
        return <div>course Not Found</div>;
    }

    const getCourse = ()=>{
      Axios.get(`http://localhost:2895/courses/${
       id
    }`).then(async (res) => {
       setCourses(res.data)
    })
    
    }
    const getCoursesTeachers = async () => {
     Axios.get(`http://localhost:2895/courses/retreivecourseteachers/${
       id
    }`).then((res) => {
       
        if (res.data.teachers) {
          setCoursesTeachers(res.data.teachers);
          console.log(res.data.teachers)
          var data = [];
          data = res.data.teachers;

          var loopData = ''
          var i;

          for (i = 0; i < data.length; i++) {
              loopData += `<li>${
                  data[i].name
              }</li>`
          }
      }

    })
        


    };


    useEffect(() => { /* setCourses(coursesListe)


        getCoursesTeachers() */
        if(id)
        {
          getCourse()
          getCoursesTeachers()
        }
       


    }, [id]);


    const updateCourses = (id) => {
        Axios.put(`http://localhost:2895/courses/${id}`, {
            name: newName,
            type: newType,
            user: newUser,
            _id: id
        }).then((response) => {
            setCoursesList(coursesList.map((val) => {
                return val._id === id ? {
                    _id: val._id,
                    name: newName,
                    user: newUser,
                    type: newType
                } : val;
            }));
        });
    };
    return (
        <CommonLayout pathList={
                ["blog", "blog details", "Gallery Layout"]
            }
            pathTitle="BLOG WITH gallery-layout">
            <section className="agency blog-sec blog-sidebar single_blog_item">
                <Container>
                    <ModalBody>
                        <div className="modal-body login-modal">
                            <Nav tabs className="nav nav-pills mb-5"
                                style={
                                    {backgroundImage: `url(
                    "https://png.pngtree.com/background/20210709/original/pngtree-starting-school-summer-vacation-student-training-course-picture-image_921426.jpg")`}
                            }>
                                <NavItem style={
                                    {marginLeft: "37%"}
                                }>
                                    <NavLink className={
                                            activeTab == "1" ? "active" : ""
                                        }
                                        onClick={
                                            () => setActiveTab("1")
                                    }>
                                        Courses Details
                                    </NavLink>
                                </NavItem>
                                <NavItem>
                                    <NavLink className={
                                            activeTab == "2" ? "active" : ""
                                        }
                                        onClick={
                                            () => setActiveTab("2")
                                    }>
                                        Participants
                                    </NavLink>
                                </NavItem>
                            </Nav>
                            <TabContent className="tab-content"
                                activeTab={activeTab}>

                                <TabPane tabId="1" aria-labelledby="pills-home-tab" className="" role="tabpanel">
                                    {/* <!-- courses details --> */}
                                    <Form>
                                        <center>
                                            <div className="form-row">
                                                <FormGroup className="col-md-12">
                                                    <Label>{
                                                        courses.name
                                                    }</Label>
                                                    <Input style={
                                                            {width: "30%"}
                                                        }
                                                        className="form-control"
                                                        id="name"
                                                        placeholder="Name"
                                                        type="name"/>
                                                </FormGroup>
                                                <FormGroup className="col-md-12">
                                                    <Label>{
                                                        courses.type
                                                    }</Label>
                                                    <Input style={
                                                            {width: "30%"}
                                                        }
                                                        className="form-control"
                                                        id="type"
                                                        placeholder="Type"
                                                        type="type"/>
                                                </FormGroup>
                                            </div>
                                            <button className="btn primary-btn btn-default text-uppercase"
                                                onClick={
                                                    () => {
                                                        updateCourses(id);
                                                    }
                                            }>
                                                Update Courses
                                            </button>
                                        </center>
                                    </Form>
                                    {/* <!-- end courses form --> */} </TabPane>
                                <TabPane tabId="2" aria-labelledby="pills-profile-tab" className="" role="tabpanel">
                                    {/* <!-- participants form --> */}
                                    <div class="container">
<div class="col-md-12">
	<div class="m-portlet m-portlet--full-height ">
		<div class="m-portlet__body">
			<div class="tab-content">
				<div class="tab-pane active" id="m_widget4_tab1_content">
					<div class="m-widget4 m-widget4--progress">
          {coursesTeachers.map((item,index)=>{
              
              return(
                <div class="m-widget4__item">
							<div class="m-widget4__img m-widget4__img--pic">
								<img src="https://bootdey.com/img/Content/avatar/avatar1.png" alt=""></img>
							</div>
							<div class="m-widget4__info">
								<span class="m-widget4__title">
                {item.name}
								</span>
								
							</div>
							<div class="m-widget4__progress">
								<div class="m-widget4__progress-wrapper">
									<span class="m-widget17__progress-number">
										63%
									</span>
									<span class="m-widget17__progress-label">
										London
									</span>
									<div class="progress m-progress--sm">
										<div class="progress-bar bg-danger" role="progressbar" style={{width: "63%"}} aria-valuenow="25" aria-valuemin="0" aria-valuemax="63"></div>
									</div>
								</div>
							</div>
							<div class="m-widget4__ext">
              <button type="button" class="btn btn-outline-danger">
                <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" class="bi bi-trash" viewBox="0 0 16 16">
  <path d="M5.5 5.5A.5.5 0 0 1 6 6v6a.5.5 0 0 1-1 0V6a.5.5 0 0 1 .5-.5zm2.5 0a.5.5 0 0 1 .5.5v6a.5.5 0 0 1-1 0V6a.5.5 0 0 1 .5-.5zm3 .5a.5.5 0 0 0-1 0v6a.5.5 0 0 0 1 0V6z"></path>
  <path fill-rule="evenodd" d="M14.5 3a1 1 0 0 1-1 1H13v9a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2V4h-.5a1 1 0 0 1-1-1V2a1 1 0 0 1 1-1H6a1 1 0 0 1 1-1h2a1 1 0 0 1 1 1h3.5a1 1 0 0 1 1 1v1zM4.118 4 4 4.059V13a1 1 0 0 0 1 1h6a1 1 0 0 0 1-1V4.059L11.882 4H4.118zM2.5 3V2h11v1h-11z"></path>
</svg>
                Button
              </button>
