import React, { useContext, useEffect, useState } from "react";

import {
  Container,
  Row,
  Col,
  Label,
  Input,
  TabContent,
  TabPane,
  FormGroup,
  ModalHeader,
  ModalBody,
  Nav,
  NavItem,
  NavLink,
  Form,
} from "reactstrap";
import CommonLayout from "../../containers/common/common-layout";

import { useRouter } from "next/router";

import Axios, * as others from "axios";

import {
  Grid,
  Link,
  List,
  ListItem,
  Typography,
  Card,
  Button,
  Badge,
  CardContent,
  TextField,
  CircularProgress,
} from "@material-ui/core";
import { ListGroupItem, ListGroup } from "reactstrap";
import { useParams } from "react-router-dom";

function CoursesDetails(props) {
  const router = useRouter();
  const { id } = router.query
  const [courses, setCourses] = useState({});
  const [coursesTeachers, setCoursesTeachers] = useState([]);
  const [usersss, setUsersss] = useState({});
  const [newName, setNewName] = useState("");
  const [newType, setNewType] = useState("");
  const [newUser, setNewUser] = useState("");
  const [coursesList, setCoursesList] = useState([]);
  const [modal, setModal] = useState();
  const [activeTab, setActiveTab] = useState("1");
  /*const listItems = coursesTeachers.map((number) =>
  <li>{number}</li>
  );*/

  const toggle = () => {
    setModal(!modal);
  };

  if (!courses) {
    return <div>course Not Found</div>;
  }
  const id3 = courses.teachers;
  const getCourses = () => {
    Axios.get(`http://localhost:2895/courses/${id}`).then((response) => {
      setCourses(response.data);
    });
  };

  const getCoursesTeachers = async() => {
    console.log('id',id)
    const res = await Axios.get(
      `http://localhost:2895/courses/retreivecourseteachers/${id}`
    ).then((response) => {
      console.log(response);
      setCoursesTeachers(response.data.teachers);
      
      var data = [];
      data =response.data.teachers;
      
      var loopData = ''
          var i ;

      for(i=0; i < data.length; i++){
        loopData += `<li>${data[i].name}</li>`
      }
      
    });
  };

  const componentDidMount= ()=>{
    getCoursesTeachers()
  };
  
  useEffect(() => {
    getCourses();
    getCoursesTeachers()
  },[Axios]);



  const updateCourses = (id) => {
    Axios.put(`http://localhost:2895/courses/${id}`, {
      name: newName,
      type: newType,
      user: newUser,
      _id: id,
    }).then((response) => {
      setCoursesList(
        coursesList.map((val) => {
          return val._id === id
            ? { _id: val._id, name: newName, user: newUser, type: newType }
            : val;
        })
      );
    });
  };
  return (
    <CommonLayout
      pathList={["blog", "blog details", "Gallery Layout"]}
      pathTitle="BLOG WITH gallery-layout"
    >
      <section className="agency blog-sec blog-sidebar single_blog_item">
        <Container>
          <ModalBody>
            <div className="modal-body login-modal">
              <Nav tabs className="nav nav-pills mb-5"   style={{
                  backgroundImage: `url(
                    "https://png.pngtree.com/background/20210709/original/pngtree-starting-school-summer-vacation-student-training-course-picture-image_921426.jpg")`,
                }}>
                <NavItem  style={{ marginLeft: "37%" }}>
                  <NavLink
                    className={activeTab == "1" ? "active" : ""}
                    onClick={() => setActiveTab("1")}
                  >
                    Courses Details
                  </NavLink>
                </NavItem>
                <NavItem>
                  <NavLink
                    className={activeTab == "2" ? "active" : ""}
                    onClick={() => setActiveTab("2")}
                  >
                   Participants
                  </NavLink>
                </NavItem>
              </Nav>
              <TabContent className="tab-content" activeTab={activeTab}>
                
                <TabPane
                  tabId="1"
                  aria-labelledby="pills-home-tab"
                  className=""
                  role="tabpanel"
                >
                  {/* <!-- courses details --> */}
                  <Form>
                    <center>
                    <div className="form-row">
                      <FormGroup className="col-md-12">
                        <Label>{courses.name}</Label>
                        <Input   style={{ width: "30%" }}
                          className="form-control"
                          id="name"
                          placeholder="Name"
                          type="name"
                        />
                      </FormGroup>
                      <FormGroup className="col-md-12">
                        <Label>{courses.type}</Label>
                        <Input   style={{ width: "30%" }}
                          className="form-control"
                          id="type"
                          placeholder="Type"
                          type="type"
                        />
                      </FormGroup>
                    </div>
                    <button
                      className="btn primary-btn btn-default text-uppercase"
                      onClick={() => {
                        updateCourses(id);
                      }}
                    >
                      Update Courses
                    </button>
                    </center>
                  </Form>
                  {/* <!-- end courses form --> */}
                </TabPane>
                <TabPane
                  tabId="2"
                  aria-labelledby="pills-profile-tab"
                  className=""
                  role="tabpanel"
                >
                  {/* <!-- participants form --> */}
                  <Form>
                    <div className="form-row">
                    <div>
                    {
                        <div>

                          
                        </div>
                    }
                      </div>
                    </div>
                    <FormGroup>
                      <Label for="inputAddress">Address</Label>
                      <Input
                        className="form-control"
                        id="inputAddress"
                        placeholder="1234 Main St"
                        type="text"
                      />
                    </FormGroup>

                    <button className="btn btn-default primary-btn text-uppercase">
                      Sign Up
                    </button>
                  </Form>
                  {/* <!-- end participants form --> */}
                </TabPane>
              </TabContent>
            </div>
          </ModalBody>
        </Container>
      </section>
    </CommonLayout>
  );
}

CoursesDetails.getInitialProps = async (ctx) => {
  console.log(ctx.query.id)
  const res = await Axios.get(`http://localhost:2895/courses/${id}`).then((response) => {
    console.log(response)
  });
  const json = await res.json()
  return { stars: json.stargazers_count }
}

export default CoursesDetails;
