import React , {useState }  from 'react';
import Layout from '../containers/common/common-layout'
import axios from 'axios';


const Profile = () => {
const [file,setFile]=useState(null);

const onInputChange = (e)=>{
    setFile(e.target.files[0])
    var oFReader = new FileReader();
        oFReader.readAsDataURL(document.getElementById("uploadImage").files[0]);

        oFReader.onload = function (oFREvent) {
            document.getElementById("uploadPreview").src = oFREvent.target.result;
        };
}
const clicked=(e)=>{
    e.preventDefault();

    const formData= new FormData()
    formData.append('username','selket');
    formData.append('photo',file);
    const config ={
        headers:{
            'content-type':'multipart/form-data',
        }
    }
    axios.post("http://localhost:5000/user/uploadProfileImg",formData,config);

}
    return(
    <Layout pathList={['portfolio basic', 'basic-4 grid with title']} pathTitle="Streams">
        <div>hello</div>
        <input type="file" id="uploadImage" accept="image/*" onChange={onInputChange}></input>
        <button onClick={clicked}>update image</button>
        <img id="uploadPreview"></img>
    </Layout>
)
    }


export default Profile;
