import { Grid, List, ListItem, TableBody, TableCell, TableContainer, TableHead, TableRow, Typography } from '@material-ui/core';
import Axios from 'axios';
import { useRouter } from 'next/router';
import React, { useContext, useEffect, useReducer, useState } from 'react';
import { Card, CardBody, CardText, CardTitle, Col, ListGroup, Row, Table } from 'reactstrap';
import MessageBox from '../../components/MessageBox';
import CommonLayout from '../../containers/common/common-layout';

import { Store } from '../../utils/Store';



export default function OrderScreen() {
  const { state } = useContext(Store);
  const { userInfo } = state;

  const router = useRouter();
    const { id: orderId } = router.query
 

  const [ order,setOrder] = useState({});

  const {
    shippingAddress,
    paymentMethod,
    orderItems,
    itemsPrice,
    taxPrice,
    shippingPrice,
    totalPrice,
    isPaid,
    isDelivered,
    deliveredAt,
    paidAt,
  } = order;



  useEffect(() => {
    const fetchOrder =  () => {
      try {
       
          Axios.get(`http://localhost:5000/orders/${orderId}`, {
          headers: { authorization: `Bearer ${userInfo.token}` },
        }).then( res => {
          setOrder(res.data);
          console.log("success");
          console.log(res.data);
          
         
         });
        
      } catch (err) {
        
      }
    };

    if (!userInfo) {
      return alert("Go To Sign In")
    }
    if (!order._id || (order._id && order._id !== orderId)) {
      fetchOrder();
    }
  }, [order, userInfo, orderId]);
  return (
    <CommonLayout title={`Order ${orderId}`}>
      <h5 className="my-3">Order {orderId}</h5>
      <Grid container spacing={1}>
          <Grid item md={9} xs={12}>
           <Card>
              <List>
                <ListItem>
                  <Typography component="h2" variant="h2">
                    Shipping Address
                  </Typography>
                </ListItem>
                 
                <ListItem>
                  Status:{' '}
                  {isDelivered
                    ? `delivered at ${deliveredAt}`
                    : 'not delivered'}
                </ListItem>
              </List>
            </Card>
            <Card>
              <List>
                <ListItem>
                  <Typography component="h2" variant="h2">
                    Payment Method
                  </Typography>
                </ListItem>
                <ListItem>{paymentMethod}</ListItem>
                <ListItem>
                  Status: {isPaid ? `paid at ${paidAt}` : 'not paid'}
                </ListItem>
              </List>
            </Card>
            <Card>
              <List>
                <ListItem>
                  <Typography component="h2" variant="h2">
                    Order Items
                  </Typography>
                </ListItem>
                <ListItem>
                  <TableContainer>
                    <Table>
                      <TableHead>
                        <TableRow>
                          <TableCell>Image</TableCell>
                          <TableCell>Name</TableCell>
                          <TableCell align="right">Quantity</TableCell>
                          <TableCell align="right">Price</TableCell>
                        </TableRow>
                      </TableHead>
                      <TableBody>
                        {orderItems.map((item) => (
                          <TableRow key={item._id}>
                            <TableCell>
                              <NextLink href={`/product/${item.slug}`} passHref>
                                <Link>
                                  <Image
                                    src={item.image}
                                    alt={item.name}
                                    width={50}
                                    height={50}
                                  ></Image>
                                </Link>
                              </NextLink>
                            </TableCell>

                            <TableCell>
                              <NextLink href={`/product/${item.slug}`} passHref>
                                <Link>
                                  <Typography>{item.name}</Typography>
                                </Link>
                              </NextLink>
                            </TableCell>
                            <TableCell align="right">
                              <Typography>{item.quantity}</Typography>
                            </TableCell>
                            <TableCell align="right">
                              <Typography>${item.price}</Typography>
                            </TableCell>
                          </TableRow>
                        ))}
                      </TableBody>
                    </Table>
                  </TableContainer>
                </ListItem>
              </List>
            </Card>
            </Grid>
            </Grid>
    
  </CommonLayout>
    
  
     
  );
}
